import "./Profile.css";
import Topbar from "../../components/topbar/Topbar";
import Sidebar from '../../components/sidebar/Sidebar';
import Rightbar from '../../components/rightbar/Rightbar';
import TimeLine from '../../components/timeline/TimeLine';
import React, { useEffect, useState } from 'react';
import axios from "axios";
import { useParams } from "react-router-dom";

export default function Profile() {
	const PUBLIC_FOLDER = process.env.REACT_APP_PUBLIC_FOLDER;

	const [user, setUser] = useState({});
	const username = useParams().username;
	console.log(username);

	useEffect(() => {
		const fetchUser = async () => {
			const response = await axios.get(`/users?username=${username}`);
			console.log(response);
			setUser(response.data);
		};
		fetchUser();
	}, []);

	return (
	<>
		<Topbar />
		<div className="profile">
			<Sidebar />
				<div className="profileRight">
					<div className="profileRightTop">
						<div className="profileCover">
							<img src={user.coverPicture || PUBLIC_FOLDER + "/post/3.png"} alt="" className='profileCoverImg'/>
							<img src={user.profilePicture || PUBLIC_FOLDER + "/person/noAvatar.png"} alt="" className='profileUserImg'/>
							{/* <img src={PUBLIC_FOLDER + "/person/1.jpg"} alt="" className='profileUserImg'/> */}
							<div className="profileInfo">
								<h4 className="profileInfoName">{user.username}</h4>
								<span className="profileInfoDesc">{user.desc}</span>
							</div>
						</div>
					</div>
						<div className="profileRightBottom">
					<TimeLine username={username} />
					<Rightbar user={user} />
						</div>
				</div>
		</div>
	</>
	);
}
