import React, { useContext, useRef } from 'react';
import { loginCall } from "../../actionCalls";
import { AuthContext } from "../../state/AuthContext";
import "./Login.css";

export default function Login() {
	const email = useRef();
	const password = useRef();
	const { user, isFetching, error,} = useContext(AuthContext);

	const handleSubmit = (e) => {
		e.preventDefault();
		// console.log(email.current.value);
		// console.log(password.current.value);
		loginCall({
			email: email.current.value,
			password: password.current.value,
		},
			dispatch
		);
	};

	return (
		<div className='login'>
			<div className="loginWrapper">
				<div className="loginLeft">
				<h3 className="loginLogo">ススミダス</h3>
				<span className="loginDesc">クローズドな空間で自己内省を。</span>
				</div>
					<div className="loginRight">
						<div className="loginBox" onSubmit={(e) => handleSubmit(e)}>
							<p className="loginMsg">ログインはこちら</p>
							<input type="email" className="loginInput" placeholder='Eメール' 
							required
							ref={email}
							/>
							<input type="password" className="loginInput" placeholder='パスワード' 
							required 
							minLength="6"
							ref={password}
							/>
							<button className="loginButton">ログイン</button>
							<span className="loginForget">パスワードを忘れた方へ</span>
							<button className="loginRegisterButton">アカウントを作成
							</button>
						</div>
					</div>
			</div>
		</div>
	);
}
