import { Chat, Notifications, Search } from '@mui/icons-material';
import React, { useContext } from 'react';
import "./Topbar.css";
import { Link } from 'react-router-dom';
import {AuthContext} from "../../state/AuthContext";

export default function Topbar() {
	const {user} = useContext(AuthContext);
	const PUBLIC_FOLDER = process.env.REACT_APP_PUBLIC_FOLDER;
	return (
	<div className="topbarContainer">
		<div className="topbarLeft">
		<Link to="/" style={{ textDecoration: "none", color: "black"}}>
			<span className="logo">
				<img src={PUBLIC_FOLDER + "/post/3.png"} alt="" className='logoSize'/></span>
			</Link>
		</div>
		<div className="topbarCenter">
			<div className="searchbar">
				<Search className="searchIcon" />
				<input type="text" className="searchInput" placeholder="探し物はなんですか？"/>
			</div>
		</div>
		<div className="topbarRight">
			<div className="topbarItemIcons">
			<div className="topbarIconItem">
				<Chat />
				<span className="topbarIconBadge">1</span>
			</div>
			<div className="topbarIconItem">
			<Notifications />
				<span className="topbarIconBadge">2</span>
				</div>
				<img src={user.profilePicture ? user.profilePicture : PUBLIC_FOLDER + "person/noAvatar.png"} alt="" className="topbarImg"/>
			</div>
		</div>
	</div>
		);
}
